<script setup>
import { reactive } from 'vue';

const nome = "luciana kamon";
const meuObj = {
  nome: "luciana kamon",
  filmeFavorito: "Totoro"
}

function dizOla(nome){
  return `${nome} diz oi`;
}

const enjderecoDaImagemDoTotoro = "data:image/jpeg;base64,";

const imagemMei = "data:image/jpeg;base64,";

const botaoEstaDesabilitado = false;

const gostaDoTotoro = false;
const gostaDaMei= false;

const estaAutorizado = false;

// let contador = 0;
const estado = reactive({
  contador: 0,
  email: '',
  saldo: 5000,
  transferindo: 0,
  nomes: ['gian', 'paulo', 'luisa', 'monica'],
  nomeAInserir: '',
})

function incrementar(){
  estado.contador++;
}

function decrementar(){
  estado.contador--;
}

function alteraEmail(evento){
  estado.email = evento.target.value;
}

function mostrarSaldoFuturo(){
  const { saldo, transferindo} = estado;
  return saldo - transferindo;
}

function validaValorTransferencia(){
  const { saldo, transferindo} = estado;
  return saldo >= transferindo;
}

function cadastrarNome(){
  if(estado.nomeAInserir.length >= 3){
    estado.nomes.push(estado.nomeAInserir)
  }else{
    alert("Digite mais caracteres")
  }
}

</script>

<template>
  <h1>{{ dizOla("paula") }}</h1>
  <img v-if="gostaDoTotoro" :src="enderecoDaImagemDoTotoro" alt="">
  <img v-else-if="gostaDaMei" :src="imagemMei" alt="">
  <h2 v-else>Não curte filme Totoro</h2>

  <h1 v-if="estaAutorizado">Não possui acesso</h1>
  <h1 v-else>Não possui acesso</h1>

  <button :disabled="botaoEstaDesabilitado">enviar mensagem</button>

  <br>
  <hr>

  {{  estado.contador }}

  <button @click="incrementar" type="button">+</button>
  <button @click="decrementar" type="button">-</button>

  <br>
  <hr>

  {{ estado.email }}
  <input type="email" @keyup="alteraEmail">

  <br>
  <hr>

  Saldo: {{  estado.saldo }} <br>
  Transferindo: {{ estado.transferindo }}<br>
  Saldo depois da transferência: {{ mostrarSaldoFuturo() }}<br>
  <input class="campo" :class="{ invalido: !validaValorTransferencia() }" @keyup="evento=> estado.transferindo = evento.target.value" type="number" placeholder="Quantia para transferir">
  <button v-if="validaValorTransferencia()">Transferir</button>
  <span v-else>Valor maior que o saldo</span>

  <br>
  <hr>

  <ul>
    <li v-for="nome in estado.nomes">
      {{ nome }}
    </li>
  </ul>
  <input @keyup="evento => estado.nomeAInserir = evento.target.value" type="text" placeholder="Digite um novo nome">
  <button @click="cadastrarNome" type="button">Cadastrar nome</button>

  <h3 v-for="nome in estado.nomes">{{ nome }}</h3>

</template>

<style scoped>
  .invalido{
    outline-color: red;
    border-color: red;
  }

  .campo{
    border: 2px solid #000;
  }

</style>
